mapper.select:
SELECT USER_ID as userId
  FROM ADM_USER_INFO 
 WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO ADM_USER_INFO (
	USER_ID
  , PASSWORD
  , USER_NAME
  , EMAIL
  , PHONE
  , DEPARTMENT
  , USER_GROUP_ID
  , LANGUAGE_TYPE
  , USE_YN
  , CREATE_USER
  , CREATE_DATE
)
VALUES (
	#{userId}
  , #{password}
  , #{userName}
  , #{email}
  , #{phone}
  , #{depart}
  , #{userGroup}
  , #{langType}
  , #{use}
  , #{operator}
  , SYSDATE
)

mapper.update:
UPDATE ADM_USER_INFO
   SET EMAIL = #{email}
     , PHONE = #{phone}
     , DEPARTMENT = #{depart}
     , USER_GROUP_ID = #{userGroup}
     , LANGUAGE_TYPE = #{langType}
     , USE_YN = #{use}
     , UPDATE_USER = #{operator}
     , UPDATE_DATE = SYSDATE
 WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_ID as userId
      ,USER_ID || ':' || (SELECT TO_CHAR(sysdate, 'YYYYMMDDHH24MISS') FROM DUAL) as accessToken
  FROM ADM_USER_INFO 
 WHERE USER_ID = #{userId}
   AND PASSWORD = #{password}

mapper.update:
UPDATE ADM_USER_INFO
   SET TOKEN = #{token}
 WHERE USER_ID = #{userId}
