mapper.select:
SELECT USER_ID as userId
      ,USER_ID || ':' || (SELECT TO_CHAR(sysdate, 'YYYYMMDDHH24MISS') FROM DUAL) as accessToken
  FROM ADM_USER_INFO 
 WHERE USER_ID = #{userId}
   AND PASSWORD = #{password}

mapper.update:
UPDATE ADM_USER_INFO
   SET TOKEN = #{token}
 WHERE USER_ID = #{userId}
